function overlayText(a) {
  var b = document.createElement("div");
  if("content" in b.style) {
    document.body.className += " has-overlay";
    for(var a = document.querySelectorAll(a), c = 0;c < a.length;c++) {
      b.innerHTML = a[c].innerHTML, a[c].setAttribute("data-content", b.textContent || b.innerText)
    }
  }
}
